import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

export default function Home() {
  const [songs, setSongs] = useState([
    { title: "Blinding Lights", artist: "The Weeknd", img: "https://upload.wikimedia.org/wikipedia/en/e/e6/The_Weeknd_-_Blinding_Lights.png" },
    {title: "nakhre", artist: "Zack knight", img: "data:image/jpeg;base64," },
  {title: "aethu kari ravilum",artist: "Hari charan",img: "data:image/jpeg;base64,"

  },
    {title: "Watermelon Sugar", artist: "Harry Styles", img: "data:image/jpeg;base64," },
    { title: "Levitating", artist: "Dua Lipa", img: "data:image/jpeg;base64," },
    { title: "Shape of You", artist: "Ed Sheeran", img: "data:image/jpeg;base64," }
  ]);
  const [newSong, setNewSong] = useState({ title: "", artist: "", img: "" });
  const navigate = useNavigate();

  const handleAddSong = () => {
    if (!newSong.title) return;
    setSongs([...songs, {
      ...newSong,
      img: newSong.img || "https://source.unsplash.com/random/300x300/?music"
    }]);
    setNewSong({ title: "", artist: "", img: "" });
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div>
      <h1 className="page-title">My Playlist</h1>

      <div style={{ display: "flex", gap: "10px", marginBottom: "30px", flexWrap: "wrap" }}>
        <input
          placeholder="Song Title"
          value={newSong.title}
          onChange={(e) => setNewSong({ ...newSong, title: e.target.value })}
          style={{ flex: 1, minWidth: "200px" }}
        />
        <input
          placeholder="Artist"
          value={newSong.artist}
          onChange={(e) => setNewSong({ ...newSong, artist: e.target.value })}
          style={{ flex: 1, minWidth: "150px" }}
        />
        <input
          placeholder="Cover URL (optional)"
          value={newSong.img}
          onChange={(e) => setNewSong({ ...newSong, img: e.target.value })}
          style={{ flex: 1, minWidth: "200px" }}
        />
        <button onClick={handleAddSong} style={{ background: "#1db954", padding: "10px 20px", borderRadius: "500px" }}>
          Add
        </button>
      </div>

      <div className="playlist-grid">
        {songs.map((song, i) => (
          <motion.div
            key={i}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: i * 0.1 }}
            className="playlist-card"
          >
            <img src={song.img} alt={song.title} />
            <div className="play-button-overlay">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="black">
                <path d="M5 3L19 12L5 21V3Z" />
              </svg>
            </div>
            <h3>{song.title}</h3>
            <p>{song.artist}</p>
          </motion.div>
        ))}
      </div>

      <button
        onClick={handleLogout}
        style={{
          display: "block",
          margin: "40px auto",
          padding: "14px 32px",
          background: "#f43b47",
          borderRadius: "500px",
          color: "#fff",
          fontWeight: "bold",
          border: "none",
          cursor: "pointer"
        }}
      >
        Logout
      </button>
    </div>
  );
}