import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Mood() {
  const [mood, setMood] = useState("");
  const navigate = useNavigate();

  const moods = [
    { label: "Happy", image: "https://i.scdn.co/image/ab67616d0000b2734a5584795dc73860653a9a3e" },
    { label: "Chill", image: "data:image/jpeg;base64," },
    { label: "Sad", image: "data:image/jpeg;base64," },
    { label: "Energetic", image: "data:image/jpeg;base64," }
  ];

  const handleSubmit = () => {
    if (mood) {
      navigate(`/playlist/${encodeURIComponent(mood)}`);
    }
  };

  return (
    <div>
      <h1 className="page-title">What's Your Mood Today?</h1>

      <div className="mood-grid">
        {moods.map((m) => (
          <div
            key={m.label}
            className="mood-card"
            onClick={() => setMood(m.label)}
            style={{
              border: mood === m.label ? "3px solid #1db954" : "3px solid transparent"
            }}
          >
            <img src={m.image} alt={m.label} />
            <div className="mood-label">{m.label}</div>
          </div>
        ))}
      </div>

      <button
        onClick={handleSubmit}
        disabled={!mood}
        style={{
          display: "block",
          margin: "40px auto",
          padding: "14px 32px",
          backgroundColor: mood ? "#1db954" : "#282828",
          color: "#fff",
          border: "none",
          borderRadius: "500px",
          fontSize: "16px",
          fontWeight: "700",
          cursor: mood ? "pointer" : "not-allowed",
          transition: "0.3s"
        }}
      >
        Play {mood} Playlist
      </button>
    </div>
  );
}